void Audio_WmaUserMsg(int32 nMsg)
{
	switch (nMsg)
	{
	    case MSG_MMC_WMADEC_VOC_IRQ:
			switch (MMC_AudioWMADecVoc(WMA_PLAYER))
			{
			case MCI_ERR_END_OF_FILE:
				diag_printf("[MMC_AUDIO]MMC_AACDecRun() return FINISH!\n");
				hal_HstSendEvent(SYS_EVENT,0x65900033);
				ContinuePlayFlag = 1;
				break;
			case MCI_ERR_ERROR:
				hal_HstSendEvent(SYS_EVENT,0x65900022);
				diag_printf("[MMC_AUDIO]##WARNING##MMC_AACDecRun() return FALSE!\n");
                
				MMC_AudioWMADecClose(WMA_PLAYER);
				MCI_AudioFinished(MCI_ERR_INVALID_FORMAT);
				mmc_SetCurrMode(MMC_MODE_IDLE);//jiashuo add
				ContinuePlayFlag = 0;
				break;
			default:
				break;
			}
		break;
            
    
		case MSG_MMC_WMADEC_PCM_IRQ:
			if(MMC_AudioWMADecPcm(WMA_PLAYER)==MCI_ERR_END_OF_FILE)
			{
				hal_HstSendEvent(SYS_EVENT,0x65900044);
			}
			break;

		default:
			break;
	}
    return;

}

